import React, { useState } from 'react';
import quizData from './data/combined_questions.json';

function QuizApp() {
  const [selectedRange, setSelectedRange] = useState('all');
  const [questionCount, setQuestionCount] = useState(5);
  const [questions, setQuestions] = useState([]);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const startQuiz = () => {
    let rangeQuestions;

    if (selectedRange === 'all') {
      // 組合所有章節的題目
      rangeQuestions = quizData.chapters.flatMap(chap => chap.questions);
    } else {
      // 僅選擇特定章節的題目
      rangeQuestions = quizData.chapters.find(chap => chap.chap === selectedRange).questions;
    }

    const selectedQuestions = rangeQuestions.sort(() => 0.5 - Math.random()).slice(0, questionCount);
    setQuestions(selectedQuestions);
    setScore(0);
    setShowResult(false);
  };

  return (
    <div>
      <h1>測驗系統</h1>
      <label>選擇章節：</label>
      <select value={selectedRange} onChange={(e) => setSelectedRange(e.target.value)}>
        <option value="all">所有章節</option>
        {quizData.chapters.map(chap => (
          <option key={chap.chap} value={chap.chap}>{chap.chap}</option>
        ))}
      </select>

      <label>選擇題數：</label>
      <input
        type="number"
        value={questionCount}
        onChange={(e) => setQuestionCount(Number(e.target.value))}
      />

      <button onClick={startQuiz}>開始測驗</button>

      {questions.map((question, index) => (
        <Question
          key={index}
          question={question}
          updateScore={(isCorrect) => setScore(score + (isCorrect ? 1 : 0))}
        />
      ))}

      {showResult && <Result score={score} questionCount={questionCount} />}
    </div>
  );
}

function Question({ question, updateScore }) {
  const [selectedOption, setSelectedOption] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);

  const checkAnswer = () => {
    const isCorrect = selectedOption === question.Detail.答案;
    updateScore(isCorrect);
    setIsAnswered(true);
  };

  return (
    <div>
      <h2>{question.題目內容}</h2>
      {['A', 'B', 'C', 'D'].map(option => (
        <div key={option}>
          <input
            type="radio"
            name={`question${question.題目}`}
            value={question.細節[`選項${option}`]}
            onChange={() => setSelectedOption(question.細節[`選項${option}`])}
          />
          {question.細節[`選項${option}`]}
        </div>
      ))}
      <button onClick={checkAnswer} disabled={isAnswered}>確認答案</button>

      {isAnswered && (
        <div>
          {selectedOption === question.細節.答案 ? '正確!' : '錯誤'}
          <p>{question.細節.詳解}</p>
        </div>
      )}
    </div>
  );
}

function Result({ score, questionCount }) {
  return (
    <div>
      <h2>測驗結束！</h2>
      <p>您的分數是：{score} / {questionCount}</p>
    </div>
  );
}

export default QuizApp;
