import React, { useState } from 'react';
import quizData from './data/combined_questions.json'; // 假設將 API 內容存成 JSON 格式的文件

// 主組件
function QuizApp() {
  const [selectedRange, setSelectedRange] = useState('ch01');
  const [questionCount, setQuestionCount] = useState(5);
  const [questions, setQuestions] = useState([]);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const startQuiz = () => {
    const rangeQuestions = quizData.chapters.find(chap => chap.chap === selectedRange).questions;
    const selectedQuestions = rangeQuestions.sort(() => 0.5 - Math.random()).slice(0, questionCount);
    setQuestions(selectedQuestions);
    setScore(0);
    setShowResult(false);
  };

  return (
    <div>
      <h1>測驗系統</h1>
      <label>選擇章節：</label>
      <select value={selectedRange} onChange={(e) => setSelectedRange(e.target.value)}>
        {quizData.chapters.map(chap => (
          <option key={chap.chap} value={chap.chap}>{chap.chap}</option>
        ))}
      </select>

      <label>選擇題數：</label>
      <input type="number" value={questionCount} onChange={(e) => setQuestionCount(Number(e.target.value))} />

      <button onClick={startQuiz}>開始測驗</button>

      {questions.map((question, index) => (
        <Question
          key={index}
          question={question}
          updateScore={(isCorrect) => setScore(score + (isCorrect ? 1 : 0))}
        />
      ))}

      {showResult && <Result score={score} questionCount={questionCount} />}
    </div>
  );
}

// 單一題目組件
function Question({ question, updateScore }) {
  const [selectedOption, setSelectedOption] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);

  const checkAnswer = () => {
    const isCorrect = selectedOption === question.Detail.Ans;
    updateScore(isCorrect);
    setIsAnswered(true);
  };

  return (
    <div>
      <h2>{question.Que}</h2>
      {['A', 'B', 'C', 'D'].map(option => (
        <div key={option}>
          <input
            type="radio"
            name={`question${question.Q_Num}`}
            value={question.Detail[`Options_${option}`]}
            onChange={() => setSelectedOption(question.Detail[`Options_${option}`])}
          />
          {question.Detail[`Options_${option}`]}
        </div>
      ))}
      <button onClick={checkAnswer} disabled={isAnswered}>確認答案</button>

      {isAnswered && (
        <div>
          {selectedOption === question.Detail.Ans ? '正確!' : '錯誤'}
          <p>{question.Detail.Detailed_Answer}</p>
        </div>
      )}
    </div>
  );
}

// 測驗結果組件
function Result({ score, questionCount }) {
  return (
    <div>
      <h2>測驗結束！</h2>
      <p>您的分數是：{score} / {questionCount}</p>
    </div>
  );
}

export default QuizApp;
